    // 添加多播源检测的辅助方法
    isMulticastStream(url) {
      if (!url) return false;

      // 更完整的多播源检测正则表达式
      const patterns = [
      // 标准格式
      /[/](udp|rtp)[/]239\.\d+\.\d+\.\d+:\d+/i, /[/](udp|rtp)[/]225\.\d+\.\d+\.\d+:\d+/i, /[/](udp|rtp)[/]224\.\d+\.\d+\.\d+:\d+/i,
      // 带@的格式
      /^(udp|rtp):\/\/@239\.\d+\.\d+\.\d+:\d+/i, /^(udp|rtp):\/\/@225\.\d+\.\d+\.\d+:\d+/i, /^(udp|rtp):\/\/@224\.\d+\.\d+\.\d+:\d+/i,
      // 不带@的格式
      /^(udp|rtp):\/\/239\.\d+\.\d+\.\d+:\d+/i, /^(udp|rtp):\/\/225\.\d+\.\d+\.\d+:\d+/i, /^(udp|rtp):\/\/224\.\d+\.\d+\.\d+:\d+/i,
      // IPv6格式
      /^(udp|rtp):\/\/\[239\.\d+\.\d+\.\d+\]:\d+/i, /^(udp|rtp):\/\/\[225\.\d+\.\d+\.\d+\]:\d+/i, /^(udp|rtp):\/\/\[224\.\d+\.\d+\.\d+\]:\d+/i,
      // 其他常见格式
      /^(udp|rtp):\/\/@?239\.\d+\.\d+\.\d+[/]\d+/i, /^(udp|rtp):\/\/@?225\.\d+\.\d+\.\d+[/]\d+/i, /^(udp|rtp):\/\/@?224\.\d+\.\d+\.\d+[/]\d+/i,
      // 简单格式
      /^udp\/@239\.\d+\.\d+\.\d+:\d+/i, /^rtp\/@239\.\d+\.\d+\.\d+:\d+/i, /^udp\/@225\.\d+\.\d+\.\d+:\d+/i, /^rtp\/@225\.\d+\.\d+\.\d+:\d+/i, /^udp\/@224\.\d+\.\d+\.\d+:\d+/i, /^rtp\/@224\.\d+\.\d+\.\d+:\d+/i,
      // 带端口的格式
      /^(udp|rtp):\/\/@?239\.\d+\.\d+\.\d+:\d+/i, /^(udp|rtp):\/\/@?225\.\d+\.\d+\.\d+:\d+/i, /^(udp|rtp):\/\/@?224\.\d+\.\d+\.\d+:\d+/i,
      // 特殊格式
      /^(udp|rtp)[/](239|225|224)\.\d+\.\d+\.\d+[/]?(\d+)?/i, /^(udp|rtp):\/\/(239|225|224)\.\d+\.\d+\.\d+[/]?(\d+)?/i,
      // HTTP代理的UDP/RTP多播源格式
      /http:\/\/[^/]+[/](udp|rtp)[/]224\.\d+\.\d+\.\d+:\d+/i, /http:\/\/[^/]+[/](udp|rtp)[/]225\.\d+\.\d+\.\d+:\d+/i, /http:\/\/[^/]+[/](udp|rtp)[/]239\.\d+\.\d+\.\d+:\d+/i,
      // HTTPS代理的UDP/RTP多播源格式
      /https:\/\/[^/]+[/](udp|rtp)[/]224\.\d+\.\d+\.\d+:\d+/i, /https:\/\/[^/]+[/](udp|rtp)[/]225\.\d+\.\d+\.\d+:\d+/i, /https:\/\/[^/]+[/](udp|rtp)[/]239\.\d+\.\d+\.\d+:\d+/i];

      // 先转换为小写进行检查
      const lowerUrl = url.toLowerCase();
      return patterns.some(pattern => pattern.test(lowerUrl));
    },
    // 检查多播源的方法
    checkMulticastStreams() {
      try {
        const appSettings = localStorage.getItem('appSettings');
        if (!appSettings) {
          console.log('未找到设置配置');
          return;
        }
        let settings;
        try {
          settings = JSON.parse(appSettings);
        } catch (e) {
          console.error('解析设置失败:', e);
          return;
        }

        // 如果启用了自动清除多播源
        if (settings.autoRemoveMulticast === true) {
          console.log('开始检查多播源...');
          let removedCount = 0;
          const toRemove = [];

          // 检查所有流
          this.streamList.forEach((stream, index) => {
            if (this.isMulticastStream(stream.url)) {
              toRemove.push(index);
              removedCount++;
              console.log('发现多播源:', stream.url);
            }
          });

          // 从后向前删除
          for (let i = toRemove.length - 1; i >= 0; i--) {
            const index = toRemove[i];
            const stream = this.streamList[index];
            console.log('正在删除多播源:', stream.name, stream.url);
            this.streamList.splice(index, 1);
          }
          if (removedCount > 0) {
            console.log(`成功清除 ${removedCount} 个多播源`);
            this.$message.warning(`已自动清除 ${removedCount} 个多播源`);
            this.saveStreamList();

            // 如果当前页没有数据，跳转到上一页
            if (this.paginatedData.length === 0 && this.currentPage > 1) {
              this.currentPage--;
            }
          }
        }
      } catch (error) {
        console.error('检查多播源失败:', error);
      }
    },
    async checkStream(stream) {
      // 优先从 appSettings 中读取 FFprobe 路径
      let ffprobePath = '';
      try {
        const appSettings = localStorage.getItem('appSettings');
        if (appSettings) {
          const settings = JSON.parse(appSettings);
          ffprobePath = settings.ffprobePath;
        }
      } catch (error) {
        console.error('读取 appSettings 失败:', error);
      }

      // 如果 appSettings 中没有，则从独立设置中读取
      if (!ffprobePath) {
        ffprobePath = localStorage.getItem('ffprobePath');
      }
      if (!ffprobePath) {
        console.error('未找到 FFprobe 路径配置');
        this.$message.error('请先在系统设置中配置FFprobe路径');
        return false;
      }

      // 检查是否是多播源
      if (this.isMulticastStream(stream.url)) {
        // 直接返回false，不再设置状态
        return false;
      }
      try {
        const response = await fetch(`${this.getBaseUrl()}/api/check-stream`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            url: stream.url,
            ffprobePath: ffprobePath
          })
        });
        if (!response.ok) throw new Error('请求失败');
        const result = await response.json();

        // 更新流信息
        stream.status = result.available ? '正常' : '异常';
        stream.resolution = result.resolution || '-';
        stream.responseTime = result.responseTime || '-';

        // 检查是否需要自动清除
        if (this.shouldRemoveStream(stream)) {
          let reason = '';
          if (stream.status === '异常') {
            reason = '异常源';
          } else if (stream.responseTime > 3000) {
            reason = '响应时间过慢';
          } else {
            reason = '分辨率过低';
          }

          // 找到当前页的所有项
          const currentPageItems = this.paginatedData;
          // 找到要删除项在当前页的索引
          const pageIndex = currentPageItems.indexOf(stream);
          // 找到要删除项在整个列表中的索引
          const listIndex = this.streamList.indexOf(stream);
          if (listIndex > -1) {
            this.streamList.splice(listIndex, 1);
            // 如果删除的是当前页的最后一项，且不是第一页，则跳转到上一页
            if (pageIndex === currentPageItems.length - 1 && this.currentPage > 1) {
              this.currentPage--;
            }
            // 添加删除原因提示
            this.$message.warning(`已自动清除直播源：${reason}`);
          }
        }
        this.saveStreamList();
        return result.available;
      } catch (error) {
        console.error('检测错误:', error);
        stream.status = '异常';
        return false;
      }
    },
    async handleBatchCheck() {
      if (this.isChecking) return;
      const uncheckedStreams = this.streamList.filter(s => !this.isPaused || this.streamList.indexOf(s) > this.pausedIndex);
      if (uncheckedStreams.length === 0) {
        this.$message.info('没有需要检测的直播源');
        return;
      }
      this.isChecking = true;
      this.shouldStop = false;
      this.totalCount = uncheckedStreams.length;
      this.checkedCount = this.isPaused ? this.checkedCount : 0;

      // 获取并发检测数量设置
      const concurrentChecks = parseInt(localStorage.getItem('concurrentChecks')) || 3;
      try {
        // 从暂停的位置或开始位置继续检测
        const startIndex = this.isPaused ? this.pausedIndex + 1 : 0;

        // 分批处理，每批次处理固定数量的流
        const batchSize = Math.min(concurrentChecks, 10); // 限制最大并发数

        for (let i = startIndex; i < uncheckedStreams.length; i += batchSize) {
          if (this.shouldStop) break;

          // 获取当前批次要检测的流
          const batch = uncheckedStreams.slice(i, i + batchSize);

          // 并发检测当前批次的所有流
          await Promise.all(batch.map(async stream => {
            if (this.shouldStop) return;
            try {
              stream.status = '检测中';
              const success = await this.checkStream(stream);
              this.checkedCount++;
              if (success) {
                this.saveState();
              }
            } catch (error) {
              console.error('检测流失败:', error);
              stream.status = '异常';
              this.checkedCount++;
            }
          }));

          // 更新暂停位置
          this.pausedIndex = i + batchSize - 1;

          // 每批次完成后保存状态
          this.saveState();
        }
        if (!this.shouldStop) {
          this.isChecking = false;
          this.isPaused = false;
          this.pausedIndex = -1;
          this.$message.success('批量检测完成');

          // 检查是否需要在批量检测完成后同步
          if (this.syncForm.autoSync && this.syncForm.syncType === 'afterCheck') {
            await this.syncToGithub(this.generateContent('txt'), this.syncForm.txtPath);
            await this.syncToGithub(this.generateContent('m3u'), this.syncForm.m3uPath);
            this.$message.success('已同步到GitHub');
          }
        }
      } catch (error) {
        console.error('批量检测错误:', error);
        this.$message.error('批量检测失败');
      } finally {
        if (this.shouldStop) {
          this.isChecking = false;
          this.isPaused = true;
          this.$message.info('检测已暂停');
        }
      }
    },
    pauseCheck() {
      this.shouldStop = true;
    },
    async resumeCheck() {
      if (this.pausedIndex >= 0) {
        this.isChecking = true;
        this.isPaused = false;
        this.shouldStop = false;
        await this.batchCheck(this.pausedIndex);
      }
    },
    async handleCheck(row) {
      row.status = '检测中';
      const success = await this.checkStream(row);
      this.$message[success ? 'success' : 'error'](`检测${success ? '成功' : '失败'}: ${row.name}`);
    },
    handleDelete(row) {
      this.$confirm('确认删除该直播源?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        const index = this.streamList.indexOf(row);
        this.streamList.splice(index, 1);
        const maxPage = Math.ceil(this.streamList.length / this.pageSize);
        if (this.currentPage > maxPage) {
          this.currentPage = Math.max(maxPage, 1);
        }
        this.saveStreamList(); // 保存更新后的列表
        this.$message.success('删除成功');
      }).catch(() => {});
    },
    async handleUrlImport() {
      const valid = await this.$refs.importForm.validate();
      if (!valid) return;
      try {
        const response = await fetch(`${this.getBaseUrl()}/api/import-url`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            url: this.importForm.url
          })
        });
        if (!response.ok) throw new Error('导入失败');
        const content = await response.text();
        const newStreams = this.parseContent(content);
        if (newStreams.length > 0) {
          // 过滤重复的直播源
          const existingUrls = new Set(this.streamList.map(s => s.url));
          const uniqueStreams = newStreams.filter(s => !existingUrls.has(s.url));
          if (uniqueStreams.length > 0) {
            this.streamList.push(...uniqueStreams);
            this.$message.success(`成功导入 ${uniqueStreams.length} 个直播源`);
            this.importUrlDialogVisible = false;

            // 导入后立即检查多播源
            this.$nextTick(() => {
              this.checkMulticastStreams();
            });
          } else {
            this.$message.info('没有新的直播源可以导入');
          }
        } else {
          this.$message.warning('未找到有效的直播源');
        }
      } catch (error) {
        this.$message.error('导入失败：' + error.message);
      }
    },
    readFile(file) {
      return new Promise((resolve, reject) => {
        const reader = new FileReader();
        reader.onload = e => resolve(e.target.result);
        reader.onerror = () => reject(new Error('文件读取失败'));
        reader.readAsText(file);
      });
    },
    parseContent(content) {
      const lines = content.split('\n');
      const streams = [];
      let currentGroup = '未分组';

      // 检测是否是 m3u 格式
      if (content.includes('#EXTM3U')) {
        let currentName = '';
        let currentGroupTitle = '';
        lines.forEach(line => {
          line = line.trim();
          if (!line) return;
          if (line.startsWith('#EXTINF:')) {
            // 尝试不同的名称提取方式
            let name = '';

            // 1. 尝试提取 tvg-name 属性
            const tvgNameMatch = line.match(/tvg-name="([^"]+)"/i);
            if (tvgNameMatch) {
              name = tvgNameMatch[1].trim();
            }

            // 2. 如果没有 tvg-name，尝试提取最后的逗号后的名称
            if (!name) {
              const lastCommaIndex = line.lastIndexOf(',');
              if (lastCommaIndex !== -1) {
                name = line.substring(lastCommaIndex + 1).trim();
              }
            }

            // 3. 如果还是没有名称，尝试提取 tvg-id
            if (!name) {
              const tvgIdMatch = line.match(/tvg-id="([^"]+)"/i);
              if (tvgIdMatch) {
                name = tvgIdMatch[1].trim();
              } else {
                // 3.1 尝试提取不带引号的 tvg-id
                const tvgIdNoQuoteMatch = line.match(/tvg-id=([^,\s]+)/i);
                if (tvgIdNoQuoteMatch) {
                  name = tvgIdNoQuoteMatch[1].trim();
                }
              }
            }
            currentName = name || '未命名';

            // 提取分组信息
            // 1. 先尝试带引号的 group-title
            const groupMatch = line.match(/group-title="([^"]+)"/i);
            if (groupMatch) {
              currentGroupTitle = groupMatch[1].trim();
            } else {
              // 2. 尝试不带引号的 group-title
              const groupNoQuoteMatch = line.match(/group-title=([^,\s]+)/i);
              if (groupNoQuoteMatch) {
                currentGroupTitle = groupNoQuoteMatch[1].trim();
              }
            }
          } else if (!line.startsWith('#') && line.trim()) {
            // 这是一个媒体URL行
            // 在添加流之前先检查是否是多播源
            if (currentName && !this.isMulticastStream(line.trim())) {
              streams.push({
                name: currentName,
                url: line.trim(),
                group: currentGroupTitle || currentGroup,
                status: '未检测',
                resolution: '-',
                responseTime: '-'
              });
            }
            currentName = ''; // 重置当前名称
          }
        });
      } else {
        // txt 格式处理
        lines.forEach(line => {
          line = line.trim();
          if (!line) return;

          // 检查是否是分组标记
          if (line.endsWith(',#genre#')) {
            currentGroup = line.replace(',#genre#', '');
            return;
          }

          // 解析直播源行
          const [name, urls] = line.split(',');
          if (!name || !urls) return;

          // 检查是否包含多个URL（用#分隔）
          const urlList = urls.split('#');

          // 为每个URL创建一个新的直播源条目，但排除多播源
          urlList.forEach(url => {
            if (url && url.trim() && !this.isMulticastStream(url.trim())) {
              streams.push({
                name: name,
                url: url.trim(),
                group: currentGroup,
                status: '未检测',
                resolution: '-',
                responseTime: '-'
              });
            }
          });
        });
      }
      return streams;
    },
    addStreamsToList(streams) {
      streams.forEach(stream => {
        this.streamList.push(stream);
      });
      this.currentPage = 1;
      this.saveStreamList(); // 保存更新后的列表
    },
    handleSizeChange(val) {
      this.pageSize = val;
