export default {
  name: 'StreamCheck',
  data() {
    return {
      loading: false,
      streamList: [],
      importUrlDialogVisible: false,
      importForm: {
        url: '',
        group: ''
      },
      importRules: {
        url: [{
          required: true,
          message: '请输入URL地址',
          trigger: 'blur'
        }, {
          type: 'url',
          message: '请输入正确的URL地址',
          trigger: 'blur'
        }],
        group: [{
          required: true,
          message: '请选择分组',
          trigger: 'change'
        }]
      },
      currentPage: 1,
      pageSize: 5,
      isChecking: false,
      isPaused: false,
      checkedCount: 0,
      totalCount: 0,
      pausedIndex: -1,
      shouldStop: false,
      isCollecting: false,
      collectDialogVisible: false,
      collectForm: {
        url: '',
        autoCollect: false,
        interval: 30
      },
      collectRules: {
        url: [{
          required: true,
          message: '请输入采集地址',
          trigger: 'blur'
        }, {
          type: 'url',
          message: '请输入正确的URL地址',
          trigger: 'blur'
        }],
        interval: [{
          required: true,
          message: '请输入采集间隔',
          trigger: 'blur'
        }]
      },
      collectTimer: null,
      generateLinkDialogVisible: false,
      generatedLink: '',
      previewContent: '',
      // 新增预览内容
      currentLinkId: '',
      scheduleDialogVisible: false,
      isScheduled: false,
      scheduleForm: {
        interval: 30
      },
      scheduleRules: {
        interval: [{
          required: true,
          message: '请输入检测间隔',
          trigger: 'blur'
        }]
      },
      scheduleTimer: null,
      nextScheduleTime: null
    };
  },
  computed: {
    paginatedData() {
      const start = (this.currentPage - 1) * this.pageSize;
      const end = start + this.pageSize;
      return this.streamList.slice(start, end);
    },
    checkButtonText() {
      if (!this.isChecking && !this.isPaused) {
        return '批量检测';
      }
      return `检测中 ${this.checkedCount}/${this.totalCount}`;
    },
    mergedStreams() {
      // 创建一个Map来存储合并后的直播源
      const streamMap = new Map();

      // 按照当前列表顺序处理每个直播源
      this.streamList.forEach(stream => {
        const key = stream.name; // 使用名称作为键

        if (streamMap.has(key)) {
          // 如果已存在相同名称的直播源，添加到URLs数组
          const existing = streamMap.get(key);
          if (!existing.urls.includes(stream.url)) {
            existing.urls.push(stream.url);
          }
        } else {
          // 如果是新的名称，创建新条目
          streamMap.set(key, {
            name: stream.name,
            urls: [stream.url],
            group: stream.group
          });
        }
      });

      // 转换回数组格式
      return Array.from(streamMap.values());
    }
  },
  created() {
    // 初始化时加载持久化的数据
    if (localStorage.getItem('persistentData') === 'true') {
      const savedList = localStorage.getItem('streamList');
      if (savedList) {
        this.streamList = JSON.parse(savedList);
      }
    }

    // 监听保存事件
    this.$root.$on('save-stream-list', this.saveStreamList);
  },
  beforeDestroy() {
    // 移除事件监听
    this.$root.$off('save-stream-list', this.saveStreamList);
    // 组件销毁前清除定时器
    if (this.collectTimer) {
      clearInterval(this.collectTimer);
    }
    // 组件销毁时清理定时器
    this.stopScheduleCheck();
  },
  methods: {
    async checkStream(stream) {
      const ffprobePath = localStorage.getItem('ffprobePath');
      if (!ffprobePath) {
        this.$message.error('请先在系统设置中配置FFprobe路径');
        return false;
      }
      try {
        const response = await fetch('http://localhost:8080/api/check-stream', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            url: stream.url,
            ffprobePath: ffprobePath
          })
        });
        if (!response.ok) throw new Error('请求失败');
        const result = await response.json();
        console.log('检测结果:', result);

        // 更新流信息
        stream.status = result.available ? '正常' : '异常';
        stream.resolution = result.resolution || '';
        stream.responseTime = result.responseTime || 0;

        // 检查是否需要自动清除
        if (this.shouldRemoveStream(stream)) {
          // 找到当前页的所有项
          const currentPageItems = this.paginatedData;
          // 找到要删除项在当前页的索引
          const pageIndex = currentPageItems.indexOf(stream);
          // 找到要删除项在整个列表中的索引
          const listIndex = this.streamList.indexOf(stream);
          if (listIndex > -1) {
            this.streamList.splice(listIndex, 1);
            // 如果删除的是当前页的最后一项，且不是第一页，则跳转到上一页
            if (pageIndex === currentPageItems.length - 1 && this.currentPage > 1) {
              this.currentPage--;
            }
          }
        }

        // 如果开启了持久化，保存更新后的列表
        this.saveStreamList();
        return result.available;
      } catch (error) {
        console.error('检测错误:', error);
        stream.status = '异常';
        return false;
      }
    },
    async handleBatchCheck() {
      if (this.isPaused) {
        this.resumeCheck();
        return;
      }
      if (this.streamList.length === 0) {
        this.$message.warning('列表为空');
        return;
      }
      this.isChecking = true;
      this.isPaused = false;
      this.shouldStop = false;
      this.checkedCount = 0;
      this.totalCount = this.streamList.length;
      await this.batchCheck(0);
    },
    async batchCheck(startIndex) {
      let successCount = 0;
      let failCount = 0;
      const validStreams = [];
      for (let i = startIndex; i < this.streamList.length; i++) {
        if (this.shouldStop) {
          this.pausedIndex = i;
          this.isPaused = true;
          this.isChecking = false;
          return;
        }
        const stream = this.streamList[i];
        try {
          const response = await fetch('http://localhost:8080/api/check-stream', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json'
            },
            body: JSON.stringify({
              url: stream.url,
              ffprobePath: localStorage.getItem('ffprobePath')
            })
          });
          if (!response.ok) throw new Error('请求失败');
          const result = await response.json();

          // 更新流信息
          stream.status = result.available ? '正常' : '异常';
          stream.resolution = result.resolution || '';
          stream.responseTime = result.responseTime || 0;
          if (result.available) {
            successCount++;
          } else {
            failCount++;
          }

          // 根据设置决定是否保留该项
          if (!this.shouldRemoveStream(stream)) {
            validStreams.push(stream);
          }
        } catch (error) {
          console.error('检测错误:', error);
          stream.status = '异常';
          failCount++;

          // 如果不自动清除异常源，则保留该项
          if (!localStorage.getItem('autoRemoveInvalid') === 'true') {
            validStreams.push(stream);
          }
        }
        this.checkedCount++;
      }

      // 如果检测完成（不是暂停状态）
      if (!this.shouldStop) {
        // 批量更新列表
        this.streamList = validStreams;

        // 更新分页
        const maxPage = Math.ceil(this.streamList.length / this.pageSize);
        if (this.currentPage > maxPage) {
          this.currentPage = Math.max(maxPage, 1);
        }

        // 保存更新后的列表
        this.saveStreamList();
        this.isChecking = false;
        this.isPaused = false;
        this.pausedIndex = -1;
        this.$message.success(`检测完成: ${successCount}个正常, ${failCount}个异常`);
      }
    },
    pauseCheck() {
      this.shouldStop = true;
    },
    async resumeCheck() {
      if (this.pausedIndex >= 0) {
        this.isChecking = true;
        this.isPaused = false;
        this.shouldStop = false;
        await this.batchCheck(this.pausedIndex);
      }
    },
    async handleCheck(row) {
      row.status = '检测中';
      const success = await this.checkStream(row);
      this.$message[success ? 'success' : 'error'](`检测${success ? '成功' : '失败'}: ${row.name}`);
    },
    handleDelete(row) {
      this.$confirm('确认删除该直播源?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        const index = this.streamList.indexOf(row);
        this.streamList.splice(index, 1);
        const maxPage = Math.ceil(this.streamList.length / this.pageSize);
        if (this.currentPage > maxPage) {
          this.currentPage = Math.max(maxPage, 1);
        }
        this.saveStreamList(); // 保存更新后的列表
        this.$message.success('删除成功');
      }).catch(() => {});
    },
    async handleUrlImport() {
      if (!this.importForm.url) {
        this.$message.warning('请输入URL地址');
        return;
      }
      this.loading = true;
      try {
        const response = await fetch(this.importForm.url);
        if (!response.ok) throw new Error('网络请求失败');
        const content = await response.text();
        const streams = this.parseContent(content);
        this.addStreamsToList(streams);
        this.importUrlDialogVisible = false;
        this.$message.success('网络文件导入成功');
      } catch (error) {
        this.$message.error('导入失败：' + error.message);
      } finally {
        this.loading = false;
      }
    },
    readFile(file) {
      return new Promise((resolve, reject) => {
        const reader = new FileReader();
        reader.onload = e => resolve(e.target.result);
        reader.onerror = () => reject(new Error('文件读取失败'));
        reader.readAsText(file);
      });
    },
    parseContent(content) {
      // 将内容按行分割
      const lines = content.split(/\r?\n/).filter(line => line.trim());
      const streams = [];

      // 解析m3u格式
      if (content.includes('#EXTM3U')) {
        let currentName = '';
        lines.forEach(line => {
          if (line.startsWith('#EXTINF:')) {
            // 提取频道名称
            currentName = line.split(',')[1]?.trim() || '';
          } else if (!line.startsWith('#') && line.trim()) {
            // 这是一个媒体URL
            streams.push({
              name: currentName || `未命名频道${streams.length + 1}`,
              url: line.trim(),
              resolution: '',
              responseTime: 0,
              status: '未检测',
              group: '未分组'
            });
            currentName = '';
          }
        });
      } else {
        // 解析txt格式
        let currentGroup = '未分组';
        lines.forEach(line => {
          const trimmedLine = line.trim();

          // 检查是否是分组标签
          if (trimmedLine.includes('#genre#')) {
            currentGroup = trimmedLine.split(',')[0].trim();
            return;
          }

          // 跳过其他#开头的行和空行
          if (trimmedLine.startsWith('#') || !trimmedLine) {
            return;
          }

          // 处理实际的直播源行
          if (trimmedLine.includes(',')) {
            // 如果行包含逗号，假设格式为：名称,URL
            const parts = trimmedLine.split(',');
            // 取最后一个部分作为URL，其余部分作为名称
            const url = parts.pop().trim();
            const name = parts.join(',').trim();
            if (url && !url.startsWith('#')) {
              // 确保URL存在且不是注释
              streams.push({
                name: name || `未命名频道${streams.length + 1}`,
                url: url,
                resolution: '',
                responseTime: 0,
                status: '未检测',
                group: currentGroup
              });
            }
          } else {
            // 如果行不包含逗号且不是注释，则整行作为URL
            streams.push({
              name: `未命名频道${streams.length + 1}`,
              url: trimmedLine,
              resolution: '',
              responseTime: 0,
              status: '未检测',
              group: currentGroup
            });
          }
        });
      }
      return streams;
    },
    addStreamsToList(streams) {
      streams.forEach(stream => {
        this.streamList.push(stream);
      });
      this.currentPage = 1;
      this.saveStreamList(); // 保存更新后的列表
    },
    handleSizeChange(val) {
      this.pageSize = val;
      // 当每页条数改变时，重置当前页为第一页
      this.currentPage = 1;
    },
    handleCurrentChange(val) {
      this.currentPage = val;
    },
    async handleFileUpload(event) {
      const file = event.target.files[0];
      if (!file) return;
      try {
        this.loading = true;
        const content = await this.readFile(file);
        const streams = this.parseContent(content);
        this.addStreamsToList(streams);
        this.$message.success('文件导入成功');
      } catch (error) {
        this.$message.error('文件导入失败：' + error.message);
      } finally {
        this.loading = false;
        // 清除文件输入，允许重复选择同一文件
        event.target.value = '';
      }
    },
    getGroupFromFileName(filename) {
      // 从文件名中提取可能的分组名
      const name = filename.toLowerCase();
      if (name.includes('cctv')) return 'CCTV';
      if (name.includes('卫视')) return '卫视';
      if (name.includes('地方')) return '地方台';
      return '未分组';
    },
    handleClearList() {
      if (this.streamList.length === 0) {
        this.$message.warning('列表已经为空');
        return;
      }
      this.$confirm('确认清空所有直播源?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        this.streamList = [];
        this.currentPage = 1;
        this.saveStreamList(); // 保存更新后的列表
        this.$message.success('列表已清空');
      }).catch(() => {});
    },
    handleCopy(row) {
      // 创建一个临时输入框来实现复制功能
      const textarea = document.createElement('textarea');
      textarea.value = row.url;
      document.body.appendChild(textarea);
      textarea.select();
      try {
        document.execCommand('copy');
        this.$message.success('链接已复制到剪贴板');
      } catch (err) {
        this.$message.error('复制失败');
      } finally {
        document.body.removeChild(textarea);
      }
    },
    shouldRemoveStream(stream) {
      // 检查是否需要清除异常源
      if (localStorage.getItem('autoRemoveInvalid') === 'true' && stream.status === '异常') {
        return true;
      }

      // 检查是否需要清除低分辨率源
      if (localStorage.getItem('autoRemoveLowRes') === 'true' && stream.resolution !== '未知') {
        const [width, height] = stream.resolution.split('x').map(Number);
        if (width < 1280 || height < 720) {
          return true;
        }
      }
      return false;
    },
    saveStreamList() {
      if (localStorage.getItem('persistentData') === 'true') {
        localStorage.setItem('streamList', JSON.stringify(this.streamList));
      }
    },
    handleCollectMode() {
      if (this.isCollecting) {
        this.stopCollecting();
      } else {
        this.collectDialogVisible = true;
      }
    },
    async startCollecting() {
      this.$refs.collectForm.validate(async valid => {
        if (valid) {
          this.collectDialogVisible = false;
          this.isCollecting = true;

          // 立即执行一次采集
          await this.collect();

          // 如果开启了自动采集，设置定时器
          if (this.collectForm.autoCollect) {
            this.collectTimer = setInterval(async () => {
              await this.collect();
            }, this.collectForm.interval * 60 * 1000); // 转换为毫秒
          }
        }
      });
    },
    stopCollecting() {
      this.isCollecting = false;
      if (this.collectTimer) {
        clearInterval(this.collectTimer);
        this.collectTimer = null;
      }
      this.$message.success('已停止采集');
    },
    async collect() {
      try {
        this.$message.info('开始采集直播源...');
        const response = await fetch(this.collectForm.url);
        if (!response.ok) throw new Error('网络请求失败');
        const content = await response.text();
        const newStreams = this.parseContent(content);

        // 过滤出新的直播源
        const existingUrls = new Set(this.streamList.map(s => s.url));
        const uniqueStreams = newStreams.filter(s => !existingUrls.has(s.url));
        if (uniqueStreams.length > 0) {
          this.addStreamsToList(uniqueStreams);
          this.$message.success(`采集成功: 新增${uniqueStreams.length}个直播源`);
        } else {
          this.$message.info('未发现新的直播源');
        }
      } catch (error) {
        this.$message.error('采集失败：' + error.message);
        // 如果是单次采集模式，出错时停止采集
        if (!this.collectForm.autoCollect) {
          this.stopCollecting();
        }
      }
    },
    async handleGenerateLink() {
      try {
        this.generateLinkDialogVisible = true;

        // 生成内容
        const content = this.generateContent();

        // 创建或更新链接内容
        const response = await fetch('http://localhost:8080/api/generate-link', {
          method: 'POST',
          headers: {
            'Content-Type': 'text/plain'
          },
          body: content
        });
        if (!response.ok) {
          throw new Error('生成链接失败');
        }

        // 使用返回的固定链接
        this.generatedLink = await response.text();
      } catch (error) {
        this.$message.error('生成链接失败：' + error.message);
      }
    },
    generateContent() {
      let content = '';
      let currentGroup = '';

      // 按组生成内容
      this.mergedStreams.forEach(stream => {
        // 如果组发生变化，添加组标记
        if (stream.group !== currentGroup) {
          currentGroup = stream.group;
          content += `${currentGroup},#genre#\n`;
        }

        // 合并相同名称的链接，用#连接
        content += `${stream.name},${stream.urls.join('#')}\n`;
      });
      return content.trim();
    },
    copyGeneratedLink() {
      const textarea = document.createElement('textarea');
      textarea.value = this.generatedLink;
      document.body.appendChild(textarea);
      textarea.select();
      try {
        document.execCommand('copy');
        this.$message.success('链接已复制到剪贴板');
      } catch (err) {
        this.$message.error('复制失败');
      } finally {
        document.body.removeChild(textarea);
      }
    },
    downloadTxtFile() {
      const blob = new Blob([this.generatedLink], {
        type: 'text/plain;charset=utf-8'
      });
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `直播源列表_${new Date().toLocaleDateString()}.txt`;
      document.body.appendChild(link);
      link.click();

      // 清理
      window.URL.revokeObjectURL(url);
      document.body.removeChild(link);
      this.$message.success('文件下载成功');
    },
    // 修改更新链接内容的方法
    async updateLinkContent() {
      try {
        const content = this.generateContent();
        await fetch('http://localhost:8080/api/update-content', {
          method: 'POST',
          headers: {
            'Content-Type': 'text/plain'
          },
          body: content
        });
      } catch (error) {
        console.error('更新链接内容失败：', error);
      }
    },
    handleScheduleCheck() {
      if (this.isScheduled) {
        this.stopScheduleCheck();
      } else {
        this.scheduleDialogVisible = true;
      }
    },
    startScheduleCheck() {
      this.$refs.scheduleForm.validate(valid => {
        if (valid) {
          this.scheduleDialogVisible = false;
          this.isScheduled = true;
          this.setupSchedule();
          this.$message.success('定时检测已开启');
        }
      });
    },
    stopScheduleCheck() {
      if (this.scheduleTimer) {
        clearInterval(this.scheduleTimer);
        this.scheduleTimer = null;
      }
      this.isScheduled = false;
      this.nextScheduleTime = null;
      this.$message.success('定时检测已停止');
    },
    setupSchedule() {
      // 清除现有定时器
      if (this.scheduleTimer) {
        clearInterval(this.scheduleTimer);
      }

      // 设置检查间隔
      let interval = 60000; // 默认每分钟检查一次

      // 计算下次执行时间
      this.nextScheduleTime = this.calculateNextTime();
      this.scheduleTimer = setInterval(() => {
        const now = new Date();

        // 如果当前正在检测中，跳过这次执行
        if (this.isChecking) {
          return;
        }

        // 检查是否到达执行时间
        if (now >= this.nextScheduleTime) {
          this.handleBatchCheck();
          // 计算下一次执行时间
          this.nextScheduleTime = this.calculateNextTime();
        }
      }, interval);
    },
    calculateNextTime() {
      const now = new Date();
      let next = new Date();
      let hours, minutes;
      switch (this.scheduleForm.type) {
        case 'minute':
          next.setMinutes(now.getMinutes() + 1, 0, 0);
          break;
        case 'day':
          // 修复每天检测的时间计算
          if (typeof this.scheduleForm.time === 'string') {
            [hours, minutes] = this.scheduleForm.time.split(':');
          } else {
            // 处理 el-time-picker 返回的 Date 对象
            hours = this.scheduleForm.time.getHours();
            minutes = this.scheduleForm.time.getMinutes();
          }
          next.setHours(parseInt(hours), parseInt(minutes), 0, 0);
          if (next <= now) {
            next.setDate(next.getDate() + 1);
          }
          break;
        case 'month':
          // 修复每月检测的时间计算
          next = new Date();
          next.setDate(this.scheduleForm.day);
          next.setHours(0, 0, 0, 0);
          // 如果当前日期已过，设置为下个月
          if (next <= now) {
            next = new Date(now.getFullYear(), now.getMonth() + 1, this.scheduleForm.day);
          }
          // 处理月末日期问题
          if (next.getDate() !== this.scheduleForm.day) {
            // 如果设置的日期在当月不存在，使用月末最后一天
            next = new Date(next.getFullYear(), next.getMonth() + 1, 0);
          }
          break;
      }
      return next;
    }
  },
  watch: {
    // 监听列表变化，自动更新链接内容
    streamList: {
      handler() {
        this.updateLinkContent();
      },
      deep: true
    }
  }
};
